import React, { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
  const [userInput, setUserInput] = useState({
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: '',
  });

  /* 공용 함수로 관리도 가능하다. */
  const inputChangeHandler = (identifier, value) => {
    if(identifier === 'title') { 
      setUserInput((prevState) => {
        return { ...prevState, enteredTitle: value}
      })
    } else if(identifier === 'amount') {
      setUserInput((prevState) => {
        return { ...prevState, enteredAmount: value}
      })
    } else {
      setUserInput((prevState) => {
        return { ...prevState, enteredDate: value}
      })
    }
  }

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: userInput.enteredTitle,
      amount: userInput.enteredAmount,
      date: new Date(userInput.enteredDate),
    };

    console.log(expenseData);

    // form 제출이 완료되면 input 값을 다시 비워준다.
    setUserInput({
      enteredTitle: '',
      enteredAmount: '',
      enteredDate: '',
    })

  }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
          <input type="text" onChange={(event) => inputChangeHandler('title', event.target.value)}/>
        </div>
        <div className='new-expense__control'>
          <label htmlFor="">Amount</label>
          <input type="number" onChange={(event) => inputChangeHandler('amount', event.target.value)}/>
        </div>
        <div className='new-expense__control'>
          <label htmlFor="">Date</label>
          <input type="date" min='2021-01-01' max='2023-12-31' onChange={(event) => inputChangeHandler('date', event.target.value)}/>
        </div>
      </div>
      <div className='new-expense__actions'>
          <button type="submit">Add Expense</button>
        </div>
    </form>
  );
};

export default ExpenseForm;
